// =============================
// DEPENDENCIES
// =============================
// packages
import React from 'react'

// components
import Post from './Post.js'
import Form from './Form.js'

// =============================
// COMPONENT CLASS
// =============================
class Main extends React.Component {
  // ==============
  // STATE
  // ==============
  constructor(props) {
    super(props)
    this.state = {
      posts: []
    }
  }

  // ==============
  // HANDLERS
  // ==============
  fetchPosts = () => {
    fetch('/api/posts')
    .then(data => data.json())
    .then(jData => {
      this.setState({ posts: jData })
    })
  }

  // ==============
  // LIFE CYCLES
  // ==============
  componentDidMount() {
    this.fetchPosts()
  }

  // ==============
  // RENDER
  // ==============
  render () {
    return (
      <main>
        <h1>{this.props.view.pageTitle}</h1>
        { this.props.view.page === 'home'
          ? this.state.posts.map((postData) => (
              <Post
                key={postData.id}
                postData={postData}
                handleView={this.props.handleView}
              />
            ))
          : <Form/>
        }
        {this.state.posts.map((postData) => (
          <Post
            key={postData.id}
            postData={postData}
            handleView={this.props.handleView}
          />
        ))}
      </main>
    )
  }
}

// =============================
// EXPORT
// =============================
export default Main
